#include "common/http.h"
void test_gen_http_query_1()
{
	char* query;
	tommy_hashdyn *env = NULL;
	env = malloc(sizeof(tommy_hashdyn));
	tommy_hashdyn_init(env);
	query = gen_http_query(0, "/", NULL, "example.com", "testagent", NULL, 0, NULL, env, NULL);
	cut_assert_equal_string(query, "GET / HTTP/1.1\nHost: example.com\nUser-Agent: testagent\n\n");
	free(query);
}

